import React, { useState, useEffect } from 'react';

const SpeechToText = () => {
  const [transcript, setTranscript] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [isSupported, setIsSupported] = useState(true); // Added state for browser support

  useEffect(() => {
    // Check if SpeechRecognition is available in the current browser
    if ('SpeechRecognition' in window || 'webkitSpeechRecognition' in window) {
      const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();

      recognition.onstart = () => {
        setIsListening(true);
      };

      recognition.onresult = (event) => {
        const result = event.results[0][0].transcript;
        setTranscript(result);
      };

      recognition.onend = () => {
        setIsListening(false);
      };

      recognition.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsListening(false);
      };

      const toggleListening = () => {
        if (isListening) {
          recognition.stop();
        } else {
          recognition.start();
        }
      };

      // Set up recognition only if supported
      setIsSupported(true);
      setIsListening(false); // Ensure listening is initially false
      recognition.lang = 'en-US'; // Set language (modify as needed)
      recognition.continuous = true; // Continuous recognition
      recognition.interimResults = true; // Get interim results

      return () => {
        recognition.stop();
      };
    } else {
      // SpeechRecognition not supported in this browser
      setIsSupported(false);
    }
  }, []);

  return (
    <div>
      <h2>Speech to Text</h2>
      {isSupported ? (
        <>
          <button onClick={toggleListening}>
            {isListening ? 'Stop Listening' : 'Start Listening'}
          </button>
          {isListening && <p>Listening...</p>}
          <p>Transcript: {transcript}</p>
        </>
      ) : (
        <p>Speech recognition is not supported in this browser.</p>
      )}
    </div>
  );
};

export default SpeechToText;
