          />
   <div className="App">
      <AudioPlayerComponent />
    </div>

    <div className="App">
      <TextToSpeech />
    </div>

    <div className="App">
      <SpeachToText />
    </div>

          <div style={{
            backgroundColor: !isDarkMode ? '#fff' : '#704214',
