import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AWS from 'aws-sdk';

const SpeechToText = ({ onTranscription }) => {
  const [isListening, setIsListening] = useState(false);
  const [isSupported, setIsSupported] = useState(true);

  const toggleListening = () => {
    if (isListening) {
      recognition.stop();
    } else {
      recognition.start();
    }
  };

  const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();

  recognition.onstart = () => {
    setIsListening(true);
  };

  recognition.onresult = (event) => {
    const result = event.results[0][0].transcript;
    onTranscription(result); // Send transcribed text to the parent component
  };

  recognition.onend = () => {
    setIsListening(false);
  };

  recognition.onerror = (event) => {
    console.error('Speech recognition error:', event.error);
    setIsListening(false);
  };

  useEffect(() => {
    if (!('SpeechRecognition' in window) && !('webkitSpeechRecognition' in window)) {
      setIsSupported(false);
    }

    return () => {
      recognition.stop();
    };
  }, []);

  return (
    <div>
      <h2>Speech to Text</h2>
      {isSupported ? (
        <>
          <button onClick={toggleListening}>
            {isListening ? 'Stop Listening' : 'Start Listening'}
          </button>
          {isListening && <p>Listening...</p>}
        </>
      ) : (
        <p>Speech recognition is not supported in this browser.</p>
      )}
    </div>
  );
};

const TextToSpeech = ({ textToSpeak }) => {
  const [audioUrl, setAudioUrl] = useState('');
  const [message, setMessage] = useState('');

  const handleTextToSpeech = async () => {
    try {
      // Reset the audio URL to clear the previous audio
      setAudioUrl('');

      // Initialize Polly client
      AWS.config.update({
        accessKeyId: 'YOUR_AWS_ACCESS_KEY',
        secretAccessKey: 'YOUR_AWS_SECRET_KEY',
        region: 'YOUR_AWS_REGION', // AWS Polly region
      });

      const polly = new AWS.Polly();

      // Synthesize speech
      const response = await polly.synthesizeSpeech({
        Text: textToSpeak,
        OutputFormat: 'mp3',
        VoiceId: 'Matthew',
      }).promise();

      // Create a Blob object from the audio stream
      const blob = new Blob([response.AudioStream], { type: 'audio/mpeg' });
      const url = URL.createObjectURL(blob);

      // Set the audio URL to play it in the <audio> element
      setAudioUrl(url);

      setMessage('Audio synthesis complete.');
    } catch (error) {
      if (error.code === 'NoCredentialsError') {
        setMessage('AWS credentials not found. Make sure to configure your AWS credentials.');
      } else {
        setMessage(`An error occurred: ${error.message}`);
      }
    }
  };

  return (
    <div>
      <h2>Text to Speech Conversion</h2>
      <button onClick={handleTextToSpeech}>Convert to Speech</button>
      <p>{message}</p>
      {audioUrl && (
        <div>
          <h3>Audio Output</h3>
          <audio controls>
            <source src={audioUrl} type="audio/mpeg" />
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
    </div>
  );
};

const App = () => {
  const [transcribedText, setTranscribedText] = useState('');
  const [apiResponse, setApiResponse] = useState('');

  const handleTranscription = (text) => {
    setTranscribedText(text);
  };

  const handleApiResponse = (response) => {
    setApiResponse(response);
  };

  const handleSendToApi = async () => {
    try {
      const serverEndpoint = 'YOUR_API_ENDPOINT'; // Replace with your API endpoint

      // Send the transcribed text as JSON data
      const response = await axios.post(
        serverEndpoint,
        { text: transcribedText }, // Send the transcribed text
        {
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );

      const apiResponseText = response.data.response; // Assuming your server responds with 'response'

      // Use text-to-speech to play the API's response
      handleTextToSpeech(apiResponseText);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <SpeechToText onTranscription={handleTranscription} />
      {transcribedText && (
        <>
          <button onClick={handleSendToApi}>Send to API</button>
          <TextToSpeech textToSpeak={apiResponse} />
        </>
      )}
    </div>
  );
};

export default App;
